def get_chapter_and_keyterm_urls(toc_html, chapter_urls, key_terms_urls):
    # soup = BeautifulSoup(html, "html.parser")
    soup = BeautifulSoup(toc_html, "html.parser")
    
    table_of_contents = soup.find('div', class_='table-of-contents')
    
    # ONLY immediate children that are ul
    # for ul in table_of_contents.find_all('ul', recursive=False):
    #     for li in ul.find_all('li', recursive=False):
    #         a_tag = li.find('a', href=True)
    #         if a_tag:
    #             # check if a tag text starts with a number or "key"
    #             text = a_tag.get_text(strip=True).lower()
    #             href = a_tag['href']
    #             if re.match(r'^\d', text):
    #                 chapter_urls.append(href)
                
    #             elif text.startswith("key"):
    #                 key_terms_urls.append(href) 
                    
    for a in table_of_contents.find_all('a', href=True):
        text = a.get_text(strip=True).lower()
        href = a['href']
        if re.match(r'^\d', text):
            chapter_urls.append(href)
        elif text.startswith("key"):
            key_terms_urls.append(href)
    
    

