output_file = "biology_mvp.jsonl"
output_glossary_file = "biology_glossary.jsonl"
all_data = []

def table_to_text(table):
    rows = []
    for tr in table.find_all("tr"):
        cells = [td.get_text(" ", strip=True) for td in tr.find_all(["td","th"])]
        rows.append(" | ".join(cells))
    return "\n".join(rows)

def scrape_chapter(url):
    print(f"Scraping {url}")
    html = requests.get(url).text
    soup = BeautifulSoup(html, "html.parser")

    # Chapter title
    chapter_title_tag = soup.find('h1')
    chapter_title = chapter_title_tag.get_text(strip=True) if chapter_title_tag else "Unknown Chapter"
    
    # scrape first section (untitled)
    #<div data-type="page" id="page_5b380d1e-9a27-4abf-9d1b-60c00411aca7" class="chapter-content-module" data-book-content="true"><div data-type="abstract" class="ui-has-child-title"><header><h2 data-type="title">Learning Objectives</h2></header><section><p id="para-00001">By the end of this section, you will be able to do the following:</p><ul id="list-00001">
    container_div = soup.find('div', class_='chapter-content-module', attrs={"data-book-content": "true"})
    
    # tag = p AND innerHTML != By the end of this section, you will be able to do the following:
    for p_elm in container_div.find_all('p'):
        text = p_elm.get_text(strip=True)
        if text:
            # if text != "By the end of this section, you will be able to do the following:":
            if text and not text.startswith("By the end of this section"):
                all_data.append({
                    "chapter": chapter_title,
                    "section": "intro",
                    "subsection": "intro",
                    "text": text,
                    "url": url
                })
    
    for table_div in container_div.find_all('div', class_='os-table'):
        table_text = table_to_text(table_div)
        all_data.append({
            "chapter": chapter_title,
            "section": "intro",
            "subsection": "intro",
            "text": table_text,
            "url": url
        })

    # Sections are usually h2 tags
    for section_tag in soup.find_all('h2'):
        section_title = section_tag.get_text(strip=True)
        # Collect paragraphs until next h2
        para_list = []
        for sibling in section_tag.find_next_siblings():
            if sibling.name == 'h2':
                break
            if sibling.name == 'p':
                text = sibling.get_text(strip=True)
                if text:
                    para_list.append(text)
            if sibling.name == 'div' and 'os-table' in sibling.get('class', []):
                table_text = table_to_text(sibling)
                para_list.append(table_text)
                
        # Add paragraphs to data
        for para in para_list:
            all_data.append({
                "chapter": chapter_title,
                "section": section_title,
                "subsection": "intro",
                "text": para,
                "url": url
            })
            
    # Subsections (h3)
    for subsection_tag in soup.find_all('h3'):
        subsection_title = subsection_tag.get_text(strip=True)
        # Collect paragraphs until next h2 or h3
        para_list = []
        for sibling in subsection_tag.find_next_siblings():
            if sibling.name in ['h2', 'h3']:
                break
            if sibling.name == 'p':
                text = sibling.get_text(strip=True)
                if text:
                    para_list.append(text)
            if sibling.name == 'div' and 'os-table' in sibling.get('class', []):
                table_text = table_to_text(sibling)
                para_list.append(table_text)
        # Add paragraphs to data
        for para in para_list:
            all_data.append({
                "chapter": chapter_title,
                "section": subsection_tag.find_previous('h2').get_text(strip=True) if subsection_tag.find_previous('h2') else "Unknown Section",
                "subsection": subsection_title,
                "text": para,
                "url": url
            })
            
all_glossary_terms = []

# Scrape key terms
def scrape_key_terms(url):
    print(f"Scraping key terms from {url}")
    html = requests.get(url).text
    soup = BeautifulSoup(html, "html.parser")

    # glossary_div = soup.find('div', class_='os-glossary-container')
    
    records = []
    
    # for dl in glossary_div.find_all("dl"):
    for dl in soup.find_all("dl"):
        term_tag = dl.find("dt")
        def_tag = dl.find("dd")

        if not term_tag or not def_tag:
            continue

        term = term_tag.get_text(strip=True)
        definition = def_tag.get_text(" ", strip=True)

        records.append({
            "source": "OpenStax Biology 2e",
            "term": term,
            "text": definition,
            "url": url
        })
    
    for record in records:
        all_glossary_terms.append(record)

for url in chapter_urls:
    scrape_chapter(url)
    
for url in key_terms_urls:
    scrape_key_terms(url)

# Save JSONL
with open(output_file, 'w', encoding='utf-8') as f:
    for entry in all_data:
        f.write(json.dumps(entry, ensure_ascii=False) + "\n")
        
with open(output_glossary_file, 'w', encoding='utf-8') as f:
    for entry in all_glossary_terms:
        f.write(json.dumps(entry, ensure_ascii=False) + "\n")

print(f"Scraped {len(all_data)} paragraphs into {output_file}")
print(f"Scraped {len(all_glossary_terms)} glossary terms into {output_glossary_file}")
